        <div class="h-0.5 flex-1 max-w-xs bg-gradient-to-r from-transparent via-red-700/60 to-transparent"></div>
      </div>

      <!-- Track Grid -->
      <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        <NuxtLink
          v-for="(track, index) in tracks"
          :key="track.id"
          :to="`/track/${track.id}`"
          class="track-card relative rounded-xl p-6 cursor-pointer group"
          :style="{ animationDelay: `${index * 0.1}s` }"
        >
          <!-- Track Number Badge -->
          <div
            class="absolute -top-3 -left-3 w-10 h-10 rounded-full flex items-center justify-center border-2 transition-all duration-300 shadow-lg"
            :class="track.filename
              ? 'bg-gradient-to-br from-red-700 to-red-900 border-red-500/50 group-hover:border-amber-500 group-hover:scale-110'
              : 'bg-gradient-to-br from-zinc-700 to-zinc-900 border-zinc-500/50 group-hover:border-zinc-400'"
          >
            <span v-if="track.filename" class="text-red-100 font-bold text-sm">{{ String(track.id).padStart(2, '0') }}</span>
            <span v-else class="text-zinc-400 font-bold text-sm">{{ String(track.id).padStart(2, '0') }}</span>
          </div>
          <!-- Play indicator for tracks with audio -->
          <div v-if="track.filename" class="absolute -top-2 -right-2 w-6 h-6 bg-emerald-500 rounded-full flex items-center justify-center shadow-lg animate-pulse">
            <svg class="w-3 h-3 text-white ml-0.5" fill="currentColor" viewBox="0 0 24 24">
              <path d="M8 5v14l11-7z" />
            </svg>
          </div>

          <!-- Type Badge -->
          <div class="absolute top-4 right-4">
            <span :class="['px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wider', getTypeColor(track.type)]">
              {{ getTypeIcon(track.type) }} {{ track.type }}
            </span>
          </div>

          <!-- Track Info -->
          <div class="mt-6">
            <h3 class="text-xl font-bold text-emerald-50 group-hover:text-amber-400 transition-colors duration-300 mb-2">
              {{ track.title }}
            </h3>
            <p class="text-emerald-400/70 text-sm mb-4">{{ track.subtitle }}</p>
          </div>

          <!-- Footer -->
          <div class="flex items-center justify-between mt-4 pt-4 border-t border-emerald-700/30">
            <span v-if="track.filename" class="text-emerald-500/60 text-sm font-mono">{{ track.duration }}</span>
            <span v-else class="text-zinc-500/60 text-xs uppercase">Bient√¥t</span>
            <div
              class="flex items-center gap-2 transition-colors"
              :class="track.filename ? 'text-emerald-400/50 group-hover:text-amber-500' : 'text-zinc-500/40'"
            >
              <span class="text-xs uppercase tracking-wider">{{ track.filename ? '√âcouter' : '√Ä venir' }}</span>
              <svg class="w-5 h-5 transform group-hover:translate-x-1 transition-transform" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M14 5l7 7m0 0l-7 7m7-7H3" />
              </svg>
            </div>
          </div>

          <!-- Hover Glow Effect -->
          <div class="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-500
                      bg-gradient-to-t from-amber-500/5 via-transparent to-transparent pointer-events-none"></div>
        </NuxtLink>
      </div>

